package logic;import java.io.File;import java.util.List;public interface LogicIF {    /** A text message giving some information on this implementation. */    public String getDescription();    /** Given the input directory, return the files which will be included in the GUI's table. */    public List<File> loadFiles(File directory);    /**     * Given the piece of code and list of target files, execute the code and supply the files with the results of the     * operation.     */    public void executeCode(String codeToExecute, List<OperationFile> targetFiles) throws Exception;    /** The text that explains to the user what the pattern they input in the file pattern search dialog does. */    public String getFilePatternDescription();    /**     * Given the pattern text and list of target files, mark the files of the list that have matched the pattern as selected.     */    public void matchFilePattern(String pattern, List<OperationFile> targetFiles) throws Exception;}